import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import AttendanceABI from './Attendance.json';

const CONTRACT_ADDRESS = "0x5fbdb2315678afecb367f032d93f642f64180aa3";

const App = () => {
    const [contract, setContract] = useState(null);
    const [account, setAccount] = useState("");
    const [attendance, setAttendance] = useState(0);

    useEffect(() => {
        const init = async () => {
            if (window.ethereum) {
                const provider = new ethers.Web3Provider(window.ethereum);
                const signer = await provider.getSigner();
                const contractInstance = new ethers.Contract(CONTRACT_ADDRESS, AttendanceABI.abi, signer);
                setContract(contractInstance);
                const userAccount = await signer.getAddress();
                setAccount(userAccount);

                // Fetch initial attendance count on load
                const initialAttendance = await contractInstance.getAttendance(userAccount);
                setAttendance(initialAttendance.toNumber());
            }
        };

        init();
    }, []);

    const markAttendance = async () => {
        if (!contract) {
            console.error("Contract not connected!");
            return;
        }

        try {
            console.log("Marking attendance for:", account);
            const tx = await contract.markAttendance();  // Calls markAttendance() function
            await tx.wait();
            alert("Attendance marked!");

            // Fetch updated attendance count
            const newCount = await contract.getAttendance(account);
            console.log("New Attendance Count:", newCount);
            setAttendance(newCount.toNumber());  // Update attendance state with the new count
        } catch (error) {
            console.error("Error marking attendance:", error);
            alert(error.reason || "Failed to mark attendance!");
        }
    };

    const registerStudent = async () => {
        if (!contract) {
            console.error("Contract not connected!");
            return;
        }

        try {
            console.log("Registering student:", account);
            const tx = await contract.registerStudent(account);  // Admin should call this!
            await tx.wait();
            alert("Student Registered!");
        } catch (error) {
            console.error("Error registering student:", error);
            alert(error.reason || "Failed to register student!");
        }
    };

    return (
        <div>
            <h1>Attendance DApp</h1>
            <button onClick={registerStudent}>Register as Student</button>
            <button onClick={markAttendance}>Mark Attendance</button>
            <p>Attendance: {attendance}</p>
        </div>
    );
};

export default App;
