import { useState, useEffect } from "react";
import { BrowserProvider, Contract } from "ethers"; // Correct import for ethers v6
import AttendanceABI from "./Attendance.json"; // Import ABI

const contractAddress = "0x9fe46736679d2d9a65f0992f2272de9f3c7fa6e0"; // Replace with actual contract address

function App() {
    const [account, setAccount] = useState("");
    const [contract, setContract] = useState(null);
    const [attendance, setAttendance] = useState(0);

    useEffect(() => {
        const connectWallet = async () => {
            if (window.ethereum) {
                try {
                    const provider = new BrowserProvider(window.ethereum);
                    const signer = await provider.getSigner();
                    const address = await signer.getAddress();
                    setAccount(address);

                    const attendanceContract = new Contract(contractAddress, AttendanceABI.abi, signer);
                    setContract(attendanceContract);

                    const count = await attendanceContract.getAttendance(address);
                    setAttendance(Number(count));
                } catch (error) {
                    console.error("Error connecting wallet:", error);
                }
            } else {
                alert("Please install MetaMask to use this DApp!");
            }
        };

        connectWallet();
    }, []);

    const registerStudent = async () => {
        if (!contract) {
            console.error("Contract not connected!");
            return;
        }

        try {
            console.log("Registering student:", account);
            const tx = await contract.registerStudent(account); // Calls Solidity function
            await tx.wait();
            alert("Student Registered!");
        } catch (error) {
            console.error("Error registering student:", error);
            alert(error.reason || "Failed to register student!");
        }
    };

    const markAttendance = async () => {
        console.log("Contract:", contract);
        console.log("Account:", account);

        if (!contract) {
            console.error("Contract not connected!");
            return;
        }

        try {
            const tx = await contract.markAttendance();  // Calls the markAttendance function
            console.log("Transaction:", tx);
            await tx.wait();
            alert("Attendance marked!");

            const newCount = await contract.getAttendance(account);
            console.log("New Attendance Count:", newCount);
            setAttendance(Number(newCount));
        } catch (error) {
            console.error("Error marking attendance:", error);
            alert(error.reason || "Failed to mark attendance!");
        }
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h1>University Attendance DApp</h1>
            <p><strong>Connected Wallet:</strong> {account || "Not connected"}</p>
            <p><strong>Attendance Count:</strong> {attendance}</p>
            <button onClick={markAttendance} style={{ padding: "10px 20px", fontSize: "16px", cursor: "pointer" }}>
                Mark Attendance
            </button>
            <button onClick={registerStudent}>Register as Student</button>
        </div>
    );
}

export default App;
