import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import AttendanceABI from './Attendance.json';

// Update this with your deployed contract address!
const CONTRACT_ADDRESS = "0xb7f8bc63bbcad18155201308c8f3540b07f84f5e";

const App = () => {
    const [contract, setContract] = useState(null);
    const [account, setAccount] = useState("");
    const [attendance, setAttendance] = useState(0);
    const [studentName, setStudentName] = useState("");
    const [studentAddress, setStudentAddress] = useState("");
    const [students, setStudents] = useState([]);

    useEffect(() => {
        const init = async () => {
            try {
                if (window.ethereum) {
                    const provider = new ethers.BrowserProvider(window.ethereum);
                    await window.ethereum.request({ method: 'eth_requestAccounts' });
                    const signer = await provider.getSigner();
                    const contractInstance = new ethers.Contract(
                        CONTRACT_ADDRESS,
                        AttendanceABI.abi,
                        signer
                    );

                    setContract(contractInstance);
                    const userAccount = await signer.getAddress();
                    setAccount(userAccount);

                    const initialAttendance = await contractInstance.getAttendance(userAccount);
                    setAttendance(Number(initialAttendance));

                    // Fetch all students
                    const studentCount = Number(await contractInstance.getStudentCount());
                    const fetchedStudents = [];
                    for (let i = 0; i < studentCount; i++) {
                        const addr = await contractInstance.studentAddresses(i);
                        const data = await contractInstance.students(addr);
                        fetchedStudents.push({
                            address: addr,
                            name: data.name,
                            count: Number(data.attendanceCount)
                        });
                    }
                    setStudents(fetchedStudents);
                } else {
                    console.error("Ethereum provider not found. Please install MetaMask.");
                }
            } catch (error) {
                console.error("Initialization error:", error);
            }
        };

        init();

        // Listen for account changes
        if (window.ethereum) {
            window.ethereum.on('accountsChanged', async (accounts) => {
                if (accounts.length > 0) {
                    setAccount(accounts[0]);
                    if (contract) {
                        const newAttendance = await contract.getAttendance(accounts[0]);
                        setAttendance(Number(newAttendance));
                    }
                } else {
                    setAccount("");
                    setAttendance(0);
                }
            });
        }
    }, [contract]);

    const markAttendance = async () => {
        if (!contract) {
            console.error("Contract not connected!");
            return;
        }

        try {
            console.log("Marking attendance for:", account);
            const tx = await contract.markAttendance(account);
            await tx.wait();
            alert("Attendance marked!");

            const newCount = await contract.getAttendance(account);
            setAttendance(Number(newCount));

            setStudents(prev =>
                prev.map(student =>
                    student.address === account
                        ? { ...student, count: Number(newCount) }
                        : student
                )
            );
        } catch (error) {
            console.error("Error marking attendance:", error);
            alert(error.message || "Failed to mark attendance!");
        }
    };

    const addStudent = async () => {
        if (!contract) {
            console.error("Contract not connected!");
            return;
        }
        if (!studentName || !studentAddress) {
            alert("Please enter both a student name and address!");
            return;
        }
        if (!ethers.isAddress(studentAddress)) {
            alert("Please enter a valid Ethereum address!");
            return;
        }

        try {
            console.log("Adding student:", studentAddress, "with name:", studentName);
            const tx = await contract.addStudent(studentAddress, studentName);
            await tx.wait();
            alert("Student Added!");

            const studentData = await contract.students(studentAddress);
            setStudents(prev => [
                ...prev.filter(student => student.address !== studentAddress),
                { address: studentAddress, name: studentName, count: Number(studentData.attendanceCount) }
            ]);
            setStudentName("");
            setStudentAddress("");
        } catch (error) {
            console.error("Error adding student:", error);
            alert(error.message || "Failed to add student!");
        }
    };

    const removeStudent = async (address) => {
        if (!contract) {
            console.error("Contract not connected!");
            return;
        }

        try {
            console.log("Removing student:", address);
            const tx = await contract.removeStudent(address);
            await tx.wait();
            alert("Student Removed!");

            setStudents(prev => prev.filter(student => student.address !== address));
            if (address === account) setAttendance(0);
        } catch (error) {
            console.error("Error removing student:", error);
            alert(error.message || "Failed to remove student!");
        }
    };

    // Check if the connected account is registered
    const isRegistered = students.some(student => student.address.toLowerCase() === account.toLowerCase());

    return (
        <div className="app-container">
            <h1>Attendance DApp</h1>
            <div className="account-info">
                <p><strong>Connected Account:</strong> {account || "Not connected"}</p>
            </div>
            <div className="action-section">
                <div className="input-group">
                    <input
                        type="text"
                        value={studentAddress}
                        onChange={(e) => setStudentAddress(e.target.value)}
                        placeholder="Enter student address"
                    />
                    <input
                        type="text"
                        value={studentName}
                        onChange={(e) => setStudentName(e.target.value)}
                        placeholder="Enter student name"
                    />
                    <button onClick={addStudent}>Add Student</button>
                </div>
                <button onClick={markAttendance} disabled={!isRegistered}>
                    Mark My Attendance
                </button>
            </div>
            <div className="attendance-display">
                <p><strong>Your Attendance:</strong> {attendance}</p>
            </div>
            <div className="student-list">
                <h2>Registered Students ({students.length})</h2>
                {students.length === 0 ? (
                    <p>No students added yet.</p>
                ) : (
                    students.map((student, index) => (
                        <div key={index} className="student-item">
                            <p>
                                {student.name} ({student.address.slice(0, 6)}...{student.address.slice(-4)}): {student.count} attendances
                            </p>
                            <button className="remove-btn" onClick={() => removeStudent(student.address)}>
                                Remove
                            </button>
                        </div>
                    ))
                )}
            </div>

            {/* Inline CSS */}
            <style>{`
                .app-container {
                    max-width: 800px;
                    margin: 40px auto;
                    padding: 20px;
                    font-family: Arial, sans-serif;
                    background-color: #f9f9f9;
                    border-radius: 8px;
                    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
                }

                h1 {
                    text-align: center;
                    color: #333;
                    margin-bottom: 20px;
                }

                .account-info {
                    background-color: #e9ecef;
                    padding: 10px;
                    border-radius: 5px;
                    margin-bottom: 20px;
                }

                .account-info p {
                    margin: 0;
                    word-break: break-all;
                }

                .action-section {
                    display: flex;
                    flex-direction: column;
                    gap: 15px;
                }

                .input-group {
                    display: flex;
                    gap: 10px;
                    align-items: center;
                }

                input {
                    flex: 1;
                    padding: 10px;
                    font-size: 16px;
                    border: 1px solid #ccc;
                    border-radius: 4px;
                    outline: none;
                    transition: border-color 0.3s;
                }

                input:focus {
                    border-color: #007bff;
                }

                button {
                    padding: 10px 20px;
                    font-size: 16px;
                    background-color: #007bff;
                    color: white;
                    border: none;
                    border-radius: 4px;
                    cursor: pointer;
                    transition: background-color 0.3s;
                }

                button:hover:not(:disabled) {
                    background-color: #0056b3;
                }

                button:active:not(:disabled) {
                    background-color: #004085;
                }

                button:disabled {
                    background-color: #6c757d;
                    cursor: not-allowed;
                }

                .attendance-display {
                    margin-top: 20px;
                    text-align: center;
                    padding: 10px;
                    background-color: #d4edda;
                    border-radius: 5px;
                    color: #155724;
                }

                .student-list {
                    margin-top: 20px;
                    padding: 15px;
                    background-color: #fff3cd;
                    border-radius: 5px;
                }

                .student-list h2 {
                    font-size: 18px;
                    color: #856404;
                    margin-bottom: 10px;
                }

                .student-item {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin: 5px 0;
                }

                .student-item p {
                    margin: 0;
                    color: #856404;
                    word-break: break-all;
                    flex: 1;
                }

                .remove-btn {
                    background-color: #dc3545;
                    padding: 5px 10px;
                    font-size: 14px;
                }

                .remove-btn:hover {
                    background-color: #c82333;
                }

                .remove-btn:active {
                    background-color: #bd2130;
                }

                /* Responsive Design */
                @media (max-width: 480px) {
                    .app-container {
                        margin: 20px;
                        padding: 15px;
                    }

                    .input-group {
                        flex-direction: column;
                    }

                    button {
                        width: 100%;
                    }

                    .student-item {
                        flex-direction: column;
                        align-items: flex-start;
                    }

                    .remove-btn {
                        width: auto;
                        margin-top: 5px;
                    }
                }
            `}</style>
        </div>
    );
};

export default App;
