import { useState, useEffect } from "react";
import { ethers } from "ethers";
import AttendanceABI from "./Attendance.json";  // Import ABI

const contractAddress = "0x5fbdb2315678afecb367f032d93f642f64180aa3"; // Replace with actual contract address
const contractABI = AttendanceABI.abi;

function App() {
    const [account, setAccount] = useState("");
    const [contract, setContract] = useState(null);
    const [attendance, setAttendance] = useState(0);

    useEffect(() => {
        const connectWallet = async () => {
            if (window.ethereum) {
                const provider = new ethers.providers.Web3Provider(window.ethereum);
                const signer = provider.getSigner();
                const address = await signer.getAddress();
                setAccount(address);
                const attendanceContract = new ethers.Contract(contractAddress, AttendanceABI, signer);
                setContract(attendanceContract);
                const count = await attendanceContract.getAttendance(address);
                setAttendance(count.toNumber());
            } else {
                alert("Install MetaMask to use this DApp!");
            }
        };
        connectWallet();
    }, []);

    const markAttendance = async () => {
        if (contract) {
            const tx = await contract.markAttendance(account);
            await tx.wait();
            alert("Attendance Marked!");
        }
    };

    return (
        <div>
            <h1>University Attendance DApp</h1>
            <p>Connected Wallet: {account}</p>
            <p>Attendance Count: {attendance}</p>
            <button onClick={markAttendance}>Mark Attendance</button>
        </div>
    );
}

export default App;
